import { Component, OnInit, Inject } from '@angular/core';
import { SingleBlogComponent } from '../single-blog/single-blog.component';
import { SingleBlogPost } from '../single-blog/single-blog.model';
import { HttpClient, HttpClientModule } from '@angular/common/http';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  singleblogposts: SingleBlogPost[] = [];

  /*constructor() {

    this.singleblogposts = [
      new SingleBlogPost("Testing", "Java Networking", "31 May 2023",
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas in pulvinar neque."),
      new SingleBlogPost("Java", "Java Networking", "31 May 2023",
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas in pulvinar neque."),
      new SingleBlogPost("Java", "Java Networking", "31 May 2023",
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas in pulvinar neque."),
    ];

  }*/

  constructor(http: HttpClient) {
    http.get<SingleBlogPost[]>("http://localhost:44456/blogpost")
      .subscribe(data => {
        this.singleblogposts = data;
      });

  }

  ngOnInit() {

  }

  //function to return list of numbers from 0 to n-1
  numSequence(n: number): Array<number> {
    return Array(n);
  }

}
